import React from "react";
import {Label, GrupoInput, Input, LeyendaError, IconoValidacion} from './../elements/Formularios'
import { faCircleCheck } from '@fortawesome/free-solid-svg-icons'

const ComponenteInput = ({estado, cambiarEstado, tipo, label, placeholder, nombre, leyendaError, expresionRegular}) => {
    const onChange = (evento) => {
        console.log(evento.target.value);
    }

    return ( 
        <div>
            <Label htmlFor={nombre}>{label}</Label>
            <GrupoInput>
                <Input 
                    type={tipo} 
                    placeholder={placeholder} 
                    id={nombre}
                    value={estado.campo}
                    onChange={onChange}
                />

                <IconoValidacion icon={faCircleCheck}/>
            </GrupoInput>
            <LeyendaError>{leyendaError}</LeyendaError>
        </div>
     );
}


export default ComponenteInput;
