import React, {useState} from "react";
import {
    Formulario, Label, ContenedorTerminos, ContenedorBotonCentrado, Boton, 
    MensajeExito, MensajeError
} from './elements/Formularios'

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCircleXmark } from '@fortawesome/free-solid-svg-icons'
import ComponenteInput from "./components/Input";


const App = () => {
    const [usuario, cambiarUsuario] = useState({ campo: '', valido: null });
    const [nombre, cambiarNombre] = useState({ campo: '', valido: null });
    const [password, cambiarPassword] = useState({ campo: '', valido: null });
    const [password2, cambiarPassword2] = useState({ campo: '', valido: null });
    const [correo, cambiarCorreo] = useState({ campo: '', valido: null });
    const [telefono, cambiarTelefono] = useState({ campo: '', valido: null });

    return ( 
        <main>
            <Formulario action="">
                <ComponenteInput 
                    tipo="text"
                    label="User"
                    placeholder="Enter your name"
                    name="usuario"
                    leyendaError="The name must have 4 to 16 digits and can only contain letters, numbers and underscore"
                />

                <ContenedorTerminos>
                    <Label>
                        <input type="checkbox" name="terminos" id="terminos"/>
                        Acepto los terminos y condiciones
                    </Label>
                </ContenedorTerminos>

                {false && <MensajeError>
                    <p>
                        <FontAwesomeIcon icon={faCircleXmark}/>
                        <b>Error:</b> Por favor rellena el formulario correctamente.
                    </p>
                </MensajeError>}

                <ContenedorBotonCentrado>
                    <Boton type="submit" >Enviar</Boton>
                    <MensajeExito>El formulario se envio exitosamente</MensajeExito>
                </ContenedorBotonCentrado>
            </Formulario>
        </main>
     );
}

export default App;
