import styled, { StyledComponent } from "styled-components";

const colors = {
    borderColor : "#0075FF",
    errorColor : "#bb2929",
    successColor : "#1ed12d"
}

const Formulario = styled.form`
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 20px;

    @media (max-width: 800px) {
        grid-template-columns: 1fr;
    }   
`;

const Label = styled.label`
    display: block;
    font-weight: 700;
    padding: 10px;
    min-height: 40px;
    cursor: pointer;
`;

export {
    Formulario,
    Label
}
